Schritt1Renderer.prototype._getTemplateImport = function() {
    var schritt;
    switch (this.sRenderStyle) {
        case "oev":
            schritt = __webpack_require__(75);
            break;
        default:
            throw new Error("unsupported render style '" + this.sRenderStyle + "'");
    }
    return schritt;
};

