function getAndOpenDocument(druckstueckBOandUrl) {
    var win = window.open("", "_blank");
    win.document.write("Das Dokument wird geladen. Bitte warten Sie ..");
    $.ajax({
        type: "POST",
        url: druckstueckBOandUrl.serviceUrl,
        data: JSON.stringify(druckstueckBOandUrl.bo),
        contentType: "application/json",
        success: function(data) {
            if (data.state === "OK") {
                win.location.href = Config.resourceUrl + "?token=" + data.contentData;
            } else {
                win.document.write("Das Dokument konnte aufgrund eines Anwendungsfehlers nicht geladen werden. Bitte versuchen Sie es spter nochmal.");
            }
        },
        error: function(){
            win.document.write("Das Dokument konnte aufgrund eines Netzwerkfehlers nicht geladen werden. Bitte versuchen Sie es spter nochmal.");
        }

    });
}

